import React, { useState, useEffect, useCallback } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import axios from "axios";
import "./App.css";
import Navbar from "./components/Navbar";
import Home from "./components/Home";
import PlaylistsList from "./components/PlaylistsList";
import PlaylistDetail from "./components/PlaylistDetail";
import CreatePlaylist from "./components/CreatePlaylist";
import SongList from "./components/SongList";
import AddSongForm from "./components/AddSongForm";

function App() {
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const API_URL = process.env.REACT_APP_API_URL || "http://localhost:8080/api";

  const fetchSongs = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`${API_URL}/songs`);
      setSongs(response.data);
      setError("");
    } catch (err) {
      setError("Error fetching songs. Please try again later.");
      console.error("Error fetching songs:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchSongs();
  }, []);

  const addSong = async (song) => {
    try {
      const response = await axios.post(`${API_URL}/songs`, song);
      setSongs([...songs, response.data]);
      return true;
    } catch (err) {
      setError("Error adding song. Please try again.");
      console.error("Error adding song:", err);
      return false;
    }
  };

  const deleteSong = async (id) => {
    try {
      await axios.delete(`${API_URL}/songs/${id}`);
      setSongs(songs.filter((song) => song.id !== id));
    } catch (err) {
      setError("Error deleting song. Please try again.");
      console.error("Error deleting song:", err);
    }
  };

  return (
    <Router>
      <div className="App">
        <Navbar />
        <main className="container">
          <div className="header">
            <h1>PlaylistsNow</h1>
            <p>Add your favorite songs to the playlist!</p>
          </div>

          {error && <div className="error-message">{error}</div>}

          <AddSongForm onAddSong={addSong} />

          {loading ? (
            <p>Loading songs...</p>
          ) : (
            <SongList songs={songs} onDeleteSong={deleteSong} />
          )}

          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/playlists" element={<PlaylistsList />} />
            <Route path="/playlists/:id" element={<PlaylistDetail />} />
            <Route path="/create" element={<CreatePlaylist />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
